import React, {Component} from 'react';
//import {Button} from "react-bootstrap"
//import { Joystick } from 'react-joystick-component';
import Config from "../scripts/config"


class Teleoperation extends Component {
  state = {ros: null};
  constructor(){
    super();
    this.init_connection();
    this.handleMove = this.handleMove.bind(this);
    this.handleStop = this.handleStop.bind(this);
  }

  init_connection(){
    this.state.ros = new window.ROSLIB.Ros();
    console.log(this.state.ros);

    this.state.ros.on("connection",()=>{
      console.log("connection is established In the Tele component!");
      this.setState({connected: true});
    });
    this.state.ros.on("close",()=>{
      console.log("connection is closed In the Tele component!");
      this.setState({connected: false});
      // try to reconnecet every 3 sec
      setTimeout(() => {
      try{
        this.state.ros.connect("ws://"+Config.ROSBRIDGE_SERVER_IP+":"+Config.RSOBRIDGE_PORT+"");
      }catch(error){
        console.log("Connection problem");
      }
    }, Config.CONNECTION_TIMER);
    });

  try{
    this.state.ros.connect("ws://"+Config.ROSBRIDGE_SERVER_IP+":"+Config.RSOBRIDGE_PORT+"");
  }catch(error){
    console.log("Connection problem");
  }
  }

  handleMove(event){
    console.log("Handle Move ")
    // Publisher cmd_vel
    var cmd_vel = new window.ROSLIB.Topic({
      ros: this.state.ros,
      name: Config.CMD_VEL,
      messageType: "geometry_msgs/Twist",
    });
    // Piblish twist mesg
    var twist = new window.ROSLIB.Message({
      linear: {
        x: event.y / 50,
        y: 0,
        z: 0
      },
      angular: {
        x: 0,
        y: 0,
        z: -event.x / 50,
      },
    });
    // Publish combined mesg
    cmd_vel.publish(twist);

  }

  handleStop(){
    console.log("Handle Stop ")
    // Publisher cmd_vel
    var cmd_vel = new window.ROSLIB.Topic({
      ros: this.state.ros,
      name: Config.CMD_VEL,
      messageType: "geometry_msgs/Twist",
    });
    // Piblish twist mesg
    var twist = new window.ROSLIB.Message({
      linear: {
        x: 0,
        y: 0,
        z: 0
      },
      angular: {
        x: 0,
        y: 0,
        z: 0,
      },
    });
    // Publish combined mesg
    cmd_vel.publish(twist);}

    call_rossrv(){
      console.log("Button clicked")

      var robotiq_gripper = new window.ROSLIB.Service({
                            ros : this.state.ros,
                            name : '/add_two_ints',
                            serviceType : 'ur_robotiq_ctl/AddTwoInts'
                            });

      var request = new window.ROSLIB.ServiceRequest({
                                a: this.state.force,
                                b : this.state.pose,
                              });

      robotiq_gripper.callService(request, function(result) {
      console.log('Result for service call on '
        + robotiq_gripper.name
        + ': '
        + result.sum);
    });
    console.log(request);

    var _2f140_force = new window.ROSLIB.Param({
      ros : this.state.ros,
      name : '/2f140_force'
    });

    _2f140_force.set(this.state.force);
    _2f140_force.get(function(value) {
      console.log('MAX VAL: ' + value);
    });
    var cmd_vel = new window.ROSLIB.Topic({
      ros: this.state.ros,
      name: '/ros_react',
      messageType: "std_msgs/Sring",
    });

    var msg = new window.ROSLIB.Message({
      data: "Hello World"
    });
    // Publish combined mesg
    cmd_vel.publish(msg);
  }

  render (){
    return(

      // <div>
      // <Joystick
      //   size={100}
      //   sticky={false}
      //   baseColor="#EEEEEE"
      //   stickColor="#BBBBBB"
      //   move={this.handleMove}
      //   stop={this.handleStop}
      //   ></Joystick>
      // </div>
      <div>
      <button type="button" onClick={() => this.call_rossrv()}>Request ROS SRV</button>
      </div>


  );
  }
}

export default Teleoperation;
