import React, {Component} from 'react';
import Config from "../scripts/config";
import {Row,Col, Container, Button} from "react-bootstrap";
import Slider, { Range } from 'rc-slider';




class Rossrv extends Component {
  state = {ros: null,
          min: 1,
          max: 100,
          step: 10,
          value: 1,
              };

  constructor(props){
    super(props);

    }
  log(value) {
        console.log(value); //eslint-disable-line
      }

    onSliderChange = value => {
    this.log(value);
    this.setState({ value });
  };
  // create a pose subscriber
  init_connection(){
    console.log("Establish map connection");

      try{
        this.state.ros = new window.ROSLIB.Ros();
        this.state.ros.connect(
          "ws://"+Config.ROSBRIDGE_SERVER_IP+":"+Config.RSOBRIDGE_PORT+""
        );
        console.log("In map components");
      }catch(error) {
        console.log("Connection problem in map components");
      }
    }

 componentDidMount(){
      this.init_connection();
    }
    onMinChange = e => {
      this.setState({
        min: +e.target.value || 0,
      });
    };

    onMaxChange = e => {
      this.setState({
        max: +e.target.value || 100,
      });
    };

    onStepChange = e => {
      this.setState({
        step: +e.target.value || 1,
      });
    }


      render() {
        const labelStyle = { minWidth: '60px', display: 'inline-block' };
        const inputStyle = { marginBottom: '10px' };
        return (
          <div>
            <label style={labelStyle}>Min: </label>
            <input
              type="number"
              value={this.state.min}
              onChange={this.onMinChange}
              style={inputStyle}
            />
            <br />
            <label style={labelStyle}>Max: </label>
            <input
              type="number"
              value={this.state.max}
              onChange={this.onMaxChange}
              style={inputStyle}
            />
            <br />
            <label style={labelStyle}>Step: </label>
            <input
              type="number"
              value={this.state.step}
              onChange={this.onStepChange}
              style={inputStyle}
            />
            <br />
            <br />
            <label style={labelStyle}>Value: </label>
            <span>{this.state.value}</span>
            <br />
            <br />
            <Slider
              value={this.state.value}
              min={this.state.min}
              max={this.state.max}
              step={this.state.step}
              onChange={this.onSliderChange}
            />
          </div>

        );
      }
}
export default Rossrv;
